CREATE TABLE `formulaire`.`information` ( `id` INT NOT NULL AUTO_INCREMENT , `nom` VARCHAR(60) NULL DEFAULT NULL , `prenom` VARCHAR(60) NULL DEFAULT NULL , `email` VARCHAR(60) NULL DEFAULT NULL , `choix` CHAR(60) NULL DEFAULT NULL , `echeance` CHAR(60) NULL DEFAULT NULL , `souhait` VARCHAR(60) NULL DEFAULT NULL , `intituler` VARCHAR(60) NULL DEFAULT NULL , `descrip` TEXT NULL DEFAULT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;